mapper.select:
SELECT B.*, EMPLOYEE.EMP_NM AS EMPNM, DEPARTMENT.DEPT_NM AS DEPTNM FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT CMT_NO, CMT_CONTENT, CMT_REGIDATE, BOARD_NO, EMP_NO FROM "COMMENT" WHERE "COMMENT".BOARD_NO = #{boardNo}
ORDER BY CMT_REGIDATE DESC ) A ) B JOIN EMPLOYEE ON B.EMP_NO = EMPLOYEE.EMP_NO JOIN DEPARTMENT ON EMPLOYEE.DEPT_CODE=DEPARTMENT.DEPT_CODE
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

