		, #{empNo,jdbcType=VARCHAR}
      )

mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM(
SELECT CMT_NO, CMT_CONTENT, CMT_REGIDATE, BOARD_NO, EMP_NO FROM "COMMENT" WHERE "COMMENT".EMP_NO = #{empNo}
ORDER BY BOARD_REGIDATE DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM(
SELECT CMT_NO, CMT_CONTENT, CMT_REGIDATE, BOARD_NO, EMP_NO FROM "COMMENT" WHERE "COMMENT".EMP_NO = #{empNo} AND "COMMENT".BOARD_NO = #{boardNo}
ORDER BY BOARD_REGIDATE DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}
